import "../App.css";
import { useState, useEffect } from "react";
import { setQuizState, setQuestionsLength } from "../actions";
import { useDispatch, useSelector } from "react-redux";

function SectionTwo() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [target, setTarget] = useState(false);
  const [questions, setQuestions] = useState([
    {
      id: 4,
      prompt: "Did you have an investment?",
      choices: [
        { option: "yes", targetQuestionId: [] },
        { option: "no", targetQuestionId: [] },
      ],
    },
    {
      id: 5,
      prompt: "How much was the investment?",
      choices: [],
    },
  ]);
  const { score } = useSelector((state) => state);
  const dispatch = useDispatch();

  console.log(questions);
  console.log("cur", currentQuestion);
  useEffect(() => {
    dispatch(setQuestionsLength(questions.length));
  }, []);

  const nextQuestion = (targetQuestionId) => {
      if (currentQuestion !== questions.length - 1) {
        setCurrentQuestion(currentQuestion + 1);
      }
    }
  

  const finishQuiz = () => {
    dispatch(setQuizState("finished"));
  };

  return (
    questions.length !== 0 && (
      <div className="Quiz">
        <h1>{questions[currentQuestion].prompt}</h1>
        <div className="questions">
          {questions[currentQuestion].choices.length !== 0 ? (
            questions[currentQuestion].choices.map((choice, index) => {
              return (
                <button
                  key={index}
                  onClick={() => {
                    nextQuestion(choice.targetQuestionId);
                    // dispatch action answered questions
                    // copy the question add key called answer with the answer
                    // then disptach this to to redux
                  }}
                >
                  {choice.option}
                </button>
              );
            })
          ) : (
           <div>numbersrgrtrretret</div>
          )}
        </div>
        {currentQuestion === questions.length - 1 && (
          <>
        <button onClick={finishQuiz} id="nextQuestion">
        Next
        </button></>
      ) }
      </div>
    )
  );
}

export default SectionTwo;
