const quizReducer = (
  state = {
    quizState: "start",
    userName: "",
    score: 0,
    questionsLength: 0,
    answeredQuestions: [],
  },
  action
) => {
  switch (action.type) {
    case "setQuizState":
      return { ...state, quizState: action.payload };
    case "setUserName":
      return { ...state, userName: action.payload };
    case "setAnsweredQuestions":
      return {
        ...state,
        answeredQuestions: [...state.answeredQuestions, action.payload],
      };
    case "reset":
      return state;
    default:
      return state;
  }
};

export default quizReducer;
