// ** Initial State
const initialState = {
  quizState: "start",
  userName: "",
  answeredQuestions: [],
};

const quizReducer = (state = initialState, action) => {
  switch (action.type) {
    case "setQuizState":
      return { ...state, quizState: action.payload };
    case "setUserName":
      return { ...state, userName: action.payload };
    case "setAnsweredQuestions":
      return {
        ...state,
        answeredQuestions: [...state.answeredQuestions, action.payload],
      };
    case "reset":
      return initialState;
    default:
      return state;
  }
};

export default quizReducer;
