import React from "react";
import "../App.css";
import { setQuizState, setUserName, reset } from "../actions";
import { useDispatch, useSelector } from "react-redux";

const EndScreen = () => {
  const dispatch = useDispatch();
  const { userName, answeredQuestions } = useSelector((state) => state);

  // useEffect(() => {
  //   addResults({ userName, score, questionsLength }).then((response) => {
  //     console.log(response.data);
  //   });
  // }, []);

  const restartQuiz = () => {
    dispatch(setUserName(""));
    dispatch(reset());
    dispatch(setQuizState("start"));
  };
  return (
    <div className="EndScreen">
      <h1>Company Name</h1>
      <h2>{userName}</h2>
      {answeredQuestions.map((question, index) => {
        return (
          <div  key={index}>
            <h1>{question.prompt}</h1>
            <button>{question.answer}</button>
          </div>
        );
      })}
      <button onClick={restartQuiz}>Submit</button>
      <button onClick={restartQuiz}>Restart Plan</button>
    </div>
  );
};

export default EndScreen;
